import {borrowPools} from './../../../query/pool/borrowingPools'
import {ethers} from 'ethers'

function FVerify(tUsername){
    if(tUsername=="Verify Now"){
        return <div className="twitterVerifiedLine2">
                Unverified
            </div>
    }
    else{
        return <div className="twitterVerifiedLine2">
                Verified
            </div>
    }
}

function IsVerified(props) {
    const data = JSON.stringify({
        query: `{
            twitterDetails(where:{id:"${window.sessionStorage.getItem("address")}"}) {
              id
                  twitterId
            }
          }          
          `, 
    })


    var options = {
        'method': 'POST',
        'url': 'https://api.thegraph.com/subgraphs/name/sublime-finance/sublime',
        'headers': {'Content-Type': 'application/json'},
        'body': data
    }
    console.log('hey')
    var result = request(options, async function (error, response) {
        if (error) throw new Error(error); 
        var body = JSON.parse(response.body)
        if (body.data.twitterDetails.length != 0){
            let name = 'user name'
            try {
                name = ethers.utils.parseBytes32String(body.data.twitterDetails[0].twitterId)
            } catch (error) {}
            props.setTUsername(name)
            // return "Doing it"
        }
    })
    return <div className="twitterVerifiedLineButton" onClick={() => props.verifyTwitter('data') }>
                {props.tUsername}
            </div>    
}

