import clsx from 'clsx';
import React from 'react';
import { useForm, useFormState } from 'react-hook-form';
import styled from 'styled-components';
import { Assets } from '../../../constants/assets';
import { CreditLineRequestArgs } from '../../../contractTranscations/creditLine/creditLineRequest';

const StyledCheckBoxRow = styled.div`
  display: flex;
  margin: 20px 0;
  input {
    width: 20px;
    height: 20px;
    margin: 0 10px 0 0;
  }
`;

const RowDiv = styled.div`
  text-align: left;
  width: 48%;
  vertical-align: top;
  margin-bottom: 5px;
`;

const AddressRowDiv = styled.div`
  text-align: left;
  border: 1px solid #ecf0f4;
  box-sizing: border-box;
  border-radius: 4px;
  padding: 13px 16px;
  width: 100%;
  margin-bottom: 10px;
`;

const LabelDiv = styled.div`
  margin-bottom: 3px;
  width: 100%;
  font-weight: 500;
  font-size: 14px;
  color: #131e40;
`;

const StyledModalBodyDiv = styled.div`
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
`;

const StyledSubmitButton = styled.button`
  width: 100%;
  height: 56px;
  line-height: 56px;
  text-align: center;
  background: linear-gradient(0deg, #ffd85f, #ffd85f), #ffffff;
  border-radius: 4px;
  font-weight: 600;
  font-size: 17px;
  color: #4e3300;
  margin-top: 10px;
  border: none;
  cursor: pointer;
  &.disabled {
    background: #ccc;
    color: #131e40;
    cursor: not-allowed;
  }
`;

const StyledInput = styled.input`
  outline: none;
  padding: 0 16px;
  width: 100%;
  display: inline-block;
  height: 50px;
  border: 1px solid #ecf0f4;
  box-sizing: border-box;
  border-radius: 4px;
  font-size: 16px;
`;

const StyledSelect = styled.select`
  outline: none;
  padding: 0 16px;
  width: 100%;
  display: inline-block;
  height: 50px;
  border: 1px solid #ecf0f4;
  box-sizing: border-box;
  border-radius: 4px;
  font-size: 16px;
`;

const AddressLabel = styled.div`
  width: 100%;
  font-weight: 500;
  font-size: 14px;
  color: #61709f;
`;

const AddressInput = styled.input`
  margin-top: 6px;
  width: 100%;
  background-color: transparent;
  border: none;
  outline: none;
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
  color: #a5adc6;
`;

export interface SendCreditLineModalProps {
  showModal: boolean;
  openModal: () => void;
  sendCreditLineRequest: (args: CreditLineRequestArgs) => void;
}

export default function SendCreditLineModal({
  showModal,
  openModal,
  sendCreditLineRequest,
}: SendCreditLineModalProps) {
  const { control, register, handleSubmit } = useForm({
    mode: 'onChange',
    defaultValues: {
      lenderAddress: null,
      borrowLimit: null,
      liquidationThreshold: null,
      borrowRate: null,
      autoLiquidation: false,
      collateralRatio: null,
      borrowAsset: Assets.borrowAsset,
      collateralAsset: Assets.collateralAsset,
    },
  });
  const onSubmit = (data: any) => {
    sendCreditLineRequest(data);
  };
  const { isValid, errors } = useFormState({
    control,
  });

  return (
    <React.Fragment>
      {showModal ? (
        <div className="pageModalOverlay">
          <form className="borrowPoolWindow" onSubmit={handleSubmit(onSubmit)}>
            <div className="borrowPoolWindowHeader">
              Request a line of Credit
              <div
                className="borrowPoolWindowHeaderButton"
                onClick={openModal}
              />
            </div>

            <div className="borrowPoolStep">
              <AddressRowDiv>
                <AddressLabel>Credit Line Lender Address</AddressLabel>
                <AddressInput
                  type="text"
                  {...register('lenderAddress', { required: true })}
                  placeholder="Enter the blockchain address"
                />
              </AddressRowDiv>

              <StyledModalBodyDiv>
                <RowDiv>
                  <LabelDiv>Borrow Limit</LabelDiv>
                  <StyledInput
                    type="number"
                    {...register('borrowLimit', { required: true })}
                  />
                </RowDiv>

                <RowDiv>
                  <LabelDiv>Borrow Asset</LabelDiv>
                  <StyledSelect {...register('borrowAsset')}>
                    <option value={Assets.borrowAsset}>TST</option>
                  </StyledSelect>
                </RowDiv>

                <RowDiv>
                  <LabelDiv>Collateral Ratio</LabelDiv>
                  <StyledInput
                    type="number"
                    {...register('collateralRatio', { required: true })}
                  />
                </RowDiv>

                <RowDiv>
                  <LabelDiv>Collateral Asset</LabelDiv>
                  <StyledSelect {...register('collateralAsset')}>
                    <option value={Assets.collateralAsset}>TST 1</option>
                  </StyledSelect>
                </RowDiv>

                <RowDiv>
                  <LabelDiv>Liquidation Threshold</LabelDiv>
                  <StyledInput
                    type="number"
                    {...register('liquidationThreshold', { required: true })}
                  />
                </RowDiv>

                <RowDiv>
                  <LabelDiv>Interest Rate</LabelDiv>
                  <StyledInput
                    type="number"
                    {...register('borrowRate', { required: true })}
                  />
                </RowDiv>

                <StyledCheckBoxRow>
                  <StyledInput
                    type="checkbox"
                    {...register('autoLiquidation')}
                  />
                  <LabelDiv>Autoliquidate</LabelDiv>
                </StyledCheckBoxRow>
              </StyledModalBodyDiv>

              <StyledSubmitButton
                className={clsx({
                  disabled: !isValid,
                })}
                disabled={!isValid}
                type="submit"
              >
                Send Credit Line Request
              </StyledSubmitButton>
            </div>
          </form>
        </div>
      ) : null}
    </React.Fragment>
  );
}
