import React from 'react'
import { useState, useEffect } from 'react'
import Header from '../../uiElements/Header'
import ActivityIndicator from '../../uiElements/ActivityIndicator'
import BorrowTabs from '../../uiElements/Tabs/BorrowTabs'
import Footer from '../../uiElements/Footer'
import { useHistory } from 'react-router-dom'
import './style.css'
import SendCreditLineModal from '../../uiElements/SendCreditLineModal'
import { requestCreditLineLender } from './../../../contractTranscations/creditLine/creditLineRequest'
import { borrowPools } from './../../../query/pool/borrowingPools'
import { getTotalBorrowedAmount, getBorrowRate ,getNextRepaymentDeadline } from './../../../utility/dashboard'

export default function Borrow() {

    const history = useHistory();
    const [isLoading, setIsLoading] = useState(false);
    const [poolBorrowData, setpoolBorrowData] = useState([])
    const [totalBorrowAmount, setTotalBorrowAmount] = useState(0);
    const [totalBorrowRate, setTotalBorrowRate] = useState(0);
    const [nextRepaymentDeadline, setNextRepaymentDeadline] = useState("");

    const address = window.sessionStorage.getItem("address");

    if (address == null || address == '0x0000000000') {
        history.push('/')
    }

    const onData = (data) => {
        setpoolBorrowData(data)
        setIsLoading(false)
    }

    useEffect(() => {
        setIsLoading(true)
        borrowPools(address, onData)
        getTotalBorrowedAmount(address, setTotalBorrowAmount)
        getBorrowRate(address, setTotalBorrowRate)
        getNextRepaymentDeadline(address, setNextRepaymentDeadline)

    }, []);


    const [showModal, setShowModal] = useState(false)

    const sendCreditLineRequest = () => {
        // alert('sendCreditLineRequest');
        requestCreditLineLender(creditLineAddr, borrowLimit, liquidationThreshold, interestRate, autoliquidate, collateralRatio, borrowAsset, collateralAsset);
        openModal();
    }

    const openModal = () => {
        setShowModal(prev => !prev)
    }

    const [creditLineAddr, setCreditLineAddr] = useState('');
    const [borrowLimit, setBorrowLimit] = useState('');
    const [borrowAsset, setBorrowAsset] = useState('');
    const [collateralRatio, setCollateralRatio] = useState('');
    const [collateralAsset, setCollateralAsset] = useState('');
    const [liquidationThreshold, setLiquidationThreshold] = useState('');
    const [interestRate, setInterestRate] = useState('');
    const [autoliquidate, setAutoliquidate] = useState('');

    return (
        <React.Fragment>
            <SendCreditLineModal showModal={showModal} openModal={openModal} creditLineAddr={creditLineAddr} setCreditLineAddr={setCreditLineAddr} borrowLimit={borrowLimit} setBorrowLimit={setBorrowLimit} borrowAsset={borrowAsset} setBorrowAsset={setBorrowAsset} collateralRatio={collateralRatio} setCollateralRatio={setCollateralRatio} collateralAsset={collateralAsset} setCollateralAsset={setCollateralAsset} liquidationThreshold={liquidationThreshold} setLiquidationThreshold={setLiquidationThreshold} interestRate={interestRate} setInterestRate={setInterestRate} autoliquidate={autoliquidate} setAutoliquidate={setAutoliquidate} sendCreditLineRequest={sendCreditLineRequest} />
            <Header />
            <div className="pageOuter">
                <div className="pageWrapper pageWrapperPadding">
                    <div className="pageTitle">
                        Borrow
                    </div>
                    <div className="cardsWrapper">

                            <div  className="dashboardCard dashboardCard3x">

                                <div className="dashboardCardValue">
                                    {totalBorrowRate !== null ? totalBorrowRate : 'no data' }
                                </div>

                                <div className="dashboardCardHint">
                                {'Current borrow rate'}
                                </div>

                            </div>
                            <div  className="dashboardCard dashboardCard3x">

                                <div className="dashboardCardValue">
                                    {totalBorrowAmount !== null ? totalBorrowAmount : 'no data' }
                                </div>

                                <div className="dashboardCardHint">
                                {'Total borrowed amount'}
                                </div>

                            </div>
                            <div  className="dashboardCard dashboardCard3x">

                                <div className="dashboardCardValue">
                                    {nextRepaymentDeadline !== null ? nextRepaymentDeadline : 'no data' }
                                </div>

                                <div className="dashboardCardHint">
                                {'next deadline'}
                                </div>

                            </div>

                     </div>
                    <div className="pageTitle pageTitleSecondary">
                        My Pools
                    </div>
                    {isLoading ?
                        <div className="d-flex align-items-center">
                            <ActivityIndicator />
                        </div>
                    :
                        <div className="dashboardTabsLine">
                            <BorrowTabs poolData={poolBorrowData} openModalRequest={openModal} />
                        </div>
                    }
                </div>
            </div>
            <Footer />
        </React.Fragment>

    )
}
