import React, { useEffect, useState } from 'react';
import './style.css';
import { lend } from './../../../contractTranscations/lend';
import { lendMoreDetailPools } from '../../../query/pool/lendPoolDetail';
import { getTotalSupple, getDataById, getFormatedTime, getDateTime } from '../../../contractTranscations/pool/lendingUtils';
import ActivityIndicator from '../ActivityIndicator';

export default function LendPoolDetailsModal({ showModal, openModal, selectedPool, lendMoreFunc }) {
  const [borrowAmount, setBorrowAmount] = useState('');
  const [dataById, setDataById] = useState(false);
  const [requesting, setRequesting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [maxAmount, setMaxAmount] = useState(0);
  const [lendData, setLendData] = useState({ BorrowAssest: { value: '' } });

  const onResponseCalback = (response) => {
    if (response.error) {
      setError(response.message);
    } else if (response.success) {
      setSuccess('You have lent successfully.');
    }
    setRequesting(false);
  };

  function lendToPool() {
    if (success) {
      setSuccess('');
      openModal();
    } else {
      if (borrowAmount) {
        setRequesting(true);
        lend(borrowAmount, selectedPool?.address, lendData.BorrowAssest.address, onResponseCalback);
      }
    }
  }

  useEffect(() => {
    setError('');
    setBorrowAmount('');
    setSuccess('');
    if (openModal) {
      lendMoreDetailPools(selectedPool?.address, setLendData);
      getTotalSupple(setMaxAmount);
      getDataById(selectedPool?.id, setDataById);
    }
  }, [openModal]);

  const getBorrowRate = () => {
    if (lendData && lendData.borrowRate) {
      return lendData.borrowRate.replace('%', '');
    }
    return 0;
  };

  const getTotalReturn = () => {
    if (lendData && selectedPool.loanDuration && borrowAmount) {
      const res =
        Number(borrowAmount) + (Number(getBorrowRate()) / 100) * Number(borrowAmount) * (Number(selectedPool.loanDuration) / (365 * 24 * 60 * 60));
      return Number(res).toFixed(7);
    }
    return 0;
  };

  const getInterest = () => {
    if (lendData && selectedPool.loanDuration && borrowAmount) {
      const res = (Number(getBorrowRate()) / 100) * Number(borrowAmount) * (Number(selectedPool.loanDuration) / (365 * 24 * 60 * 60));
      return Number(res).toFixed(7);
    }
    return 0;
  };

  const getLoanEnddate = () => {
    if (selectedPool.loanStartTime && selectedPool.loanDuration) {
      var date = new Date(Number(selectedPool.loanDuration) + Number(selectedPool.loanStartTime));
      return getDateTime(date);
    }
    return 0;
  };

  const getAmountInDecimal = () => {
    if (lendData && lendData.BorrowAssest && lendData.BorrowAssest.value) {
      const amount = String(maxAmount / Math.pow(10, lendData.BorrowAssest.decimals));
      return amount.split('e')[0];
    }
    return 0;
  };

  const onChangeAmount = (value) => {
    setError('');
    setSuccess('');
    setBorrowAmount(value);
  };

  return (
    <React.Fragment>
      {showModal ? (
        <div className="pageModalOverlay">
          <div className="lendPoolWindowSmaller">
            <div className="lendPoolWindowHeader">
              Lend
              <div className="lendPoolWindowHeaderButton" onClick={openModal}></div>
            </div>
            <div className="lendPoolStep">
              <div className="lendDetailsOwnerLineModal">
                <div className="lendDetailsOwnerLineLeft">
                  <div className="lendDetailsOwnerIcon"></div>
                  <div className="lendDetailsOwnerName">{selectedPool.name}</div>
                  <div className="lendDetailsOwnerUsername">{selectedPool.username}</div>
                </div>

                <div className="lendDetailsOwnerLineRight">
                  <div className="lendDetailsPoolStatus lendDetailsPoolStatusOrange">{selectedPool.pool_status}</div>
                </div>
              </div>
              <div className="lendPoolStepModalLine">
                <div className="lendPoolStepModalLineHint">Lend Rate</div>
                <div className="lendPoolStepModalLineValue">{selectedPool.borrow_rate}</div>
              </div>
              <div className="lendPoolStepModalLine lendPoolStepModalLine2">
                <div className="lendPoolStepModalLineHint">Maximum you can lend</div>
                <div className="lendPoolStepModalLineValue">{`${getAmountInDecimal()} ${lendData.BorrowAssest.value}`}</div>
              </div>
              <div className="lendPoolStepModalLine borrowPoolStepModalLine3">
                <div className="lendPoolStepHint">How much would you like to lend?</div>
                <div className="lendPoolStepModalAmount">
                  <div className="lendPoolStepModalAmountTop">
                    <div className="lendPoolStepModalAmountTopLeft">Amount you lend</div>
                    <div className="lendPoolStepModalAmountTopRight" onClick={() => setBorrowAmount(getAmountInDecimal())}>
                      {`Max: ${getAmountInDecimal()}`}
                    </div>
                  </div>
                  <div className="lendPoolStepModalAmountBottom">
                    <div className="lendPoolStepModalAmountBottomLeft">
                      <input
                        type="text"
                        className="lendPoolStepModalAmountBottomLeftField"
                        placeholder="Enter amount"
                        onChange={(e) => onChangeAmount(e.target.value)}
                        value={borrowAmount}
                      />
                    </div>
                    <div className="lendPoolStepModalAmountBottomRight">{lendData.BorrowAssest.value}</div>
                  </div>
                </div>
              </div>

              <div className="lendPoolGreyPad">
                <div className="lendPoolGreyPadLine">
                  <div className="lendPoolGreyPadLineHint">Total Return</div>

                  <div className="lendPoolGreyPadLineValue">{getTotalReturn()}</div>
                </div>

                <div className="lendPoolGreyPadLine">
                  <div className="lendPoolGreyPadLineHint">Interest</div>

                  <div className="lendPoolGreyPadLineValue">{getInterest()}</div>
                </div>

                <div className="lendPoolGreyPadLine">
                  <div className="lendPoolGreyPadLineHint">Repayment</div>

                  <div className="lendPoolGreyPadLineValue">{lendData.RepaymentFrequency}</div>
                </div>

                <div className="lendPoolGreyPadLine">
                  <div className="lendPoolGreyPadLineHint">Frequency</div>

                  <div className="lendPoolGreyPadLineValue">{dataById ? getFormatedTime(Number(dataById.repaymentInterval)) : '0d 0h 0m'}</div>
                </div>

                <div className="lendPoolGreyPadLine">
                  <div className="lendPoolGreyPadLineHint">Estimated loan end date</div>

                  <div className="lendPoolGreyPadLineValue">{getLoanEnddate()}</div>
                </div>
              </div>

              <div className="error-message">{error}</div>
              <div className="success-message">{success}</div>
              <div className="lendPoolStepModalLineButton button" onClick={lendToPool}>
                {requesting ? <ActivityIndicator /> : success ? 'OK' : 'Lend to pool'}
              </div>
            </div>
          </div>
        </div>
      ) : null}
    </React.Fragment>
  );
}
