import React from 'react';
import { useState, useEffect } from 'react';
import Header from '../../uiElements/Header';
import ActivityIndicator from '../../uiElements/ActivityIndicator';
import BorrowTabs from '../../uiElements/Tabs/BorrowTabs';
import Footer from '../../uiElements/Footer';
import { useHistory } from 'react-router-dom';
import './style.css';
import SendCreditLineModal from '../../uiElements/SendCreditLineModal';

export default function Borrow() {
  const history = useHistory();
  const [isLoading, setIsLoading] = useState(false);
  const [poolBorrowData, setpoolBorrowData] = useState([]);
  const [totalBorrowAmount, setTotalBorrowAmount] = useState(0);
  const [totalBorrowRate, setTotalBorrowRate] = useState(0);

  const address = window.sessionStorage.getItem('address');

  if (address == null || address == '0x0000000000') {
    history.push('/');
  }

  const onData = (data) => {
    setpoolBorrowData(data);
    setIsLoading(false);
  };

  useEffect(() => {
    setIsLoading(false);
  }, []);

  const borrowData = [
    {
      id: 1,
      borrow_rate: totalBorrowRate + '%',
      borrow_txt: 'Current borrow rate',
    },
    {
      id: 2,
      borrow_rate: '$' + totalBorrowAmount,
      borrow_txt: 'Total borrowed amount',
    },
    {
      id: 3,
      borrow_rate: '1,500 DAI',
      borrow_txt: 'Next repayment on 25th Jan 2021',
      pay: 'Pay',
    },
  ];

  const [showModal, setShowModal] = useState(false);

  const sendCreditLineRequest = () => {
    openModal();
  };

  const openModal = () => {
    setShowModal((prev) => !prev);
  };

  const [creditLineAddr, setCreditLineAddr] = useState('');
  const [borrowLimit, setBorrowLimit] = useState('');
  const [borrowAsset, setBorrowAsset] = useState('');
  const [collateralRatio, setCollateralRatio] = useState('');
  const [collateralAsset, setCollateralAsset] = useState('');
  const [liquidationThreshold, setLiquidationThreshold] = useState('');
  const [interestRate, setInterestRate] = useState('');
  const [autoliquidate, setAutoliquidate] = useState('');

  return (
    <React.Fragment>
      <SendCreditLineModal
        showModal={showModal}
        openModal={openModal}
        creditLineAddr={creditLineAddr}
        setCreditLineAddr={setCreditLineAddr}
        borrowLimit={borrowLimit}
        setBorrowLimit={setBorrowLimit}
        borrowAsset={borrowAsset}
        setBorrowAsset={setBorrowAsset}
        collateralRatio={collateralRatio}
        setCollateralRatio={setCollateralRatio}
        collateralAsset={collateralAsset}
        setCollateralAsset={setCollateralAsset}
        liquidationThreshold={liquidationThreshold}
        setLiquidationThreshold={setLiquidationThreshold}
        interestRate={interestRate}
        setInterestRate={setInterestRate}
        autoliquidate={autoliquidate}
        setAutoliquidate={setAutoliquidate}
        sendCreditLineRequest={sendCreditLineRequest}
      />
      <Header />
      <div className="pageOuter">
        <div className="pageWrapper pageWrapperPadding">
          <div className="pageTitle">Borrow</div>
          <div className="cardsWrapper">
            {borrowData.map((items, index) => (
              <div key={index} className="dashboardCard dashboardCard3x">
                <div className="dashboardCardValue">{items.borrow_rate}</div>

                <div className="dashboardCardHint">{items.borrow_txt}</div>
              </div>
            ))}
          </div>
          <div className="pageTitle pageTitleSecondary">My Pools</div>
          {isLoading ? (
            <div className="d-flex align-items-center">
              <ActivityIndicator />
            </div>
          ) : (
            <div className="dashboardTabsLine">
              <BorrowTabs poolData={poolBorrowData} openModalRequest={openModal} />
            </div>
          )}
        </div>
      </div>
      <Footer />
    </React.Fragment>
  );
}
