import React from 'react';
import { ethers } from 'ethers';
import { useState, useEffect } from 'react';
import './style.css';
import { addCollateral } from './../../../contractTranscations/addCollateral';
import { collateralborrowPools } from './../../../query/pool/addCollateral';
import ActivityIndicator from '../ActivityIndicator';

const poolContractMeta = require('../../../abi/artifacts/contracts/Pool/Pool.sol/Pool.json');
const _interface = new ethers.utils.Interface(poolContractMeta.abi);
const initializeFragement = _interface.getFunction('getCurrentCollateralRatio');

export default function AddCollateralModal({ showModal, openModal, selectedPool }) {
  const [borrowAmount, setBorrowAmount] = useState('');
  const [requesting, setRequesting] = useState(false);
  const [pooldata, setpooldata] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const [collateralRatio, setCollateralRatio] = useState(0);

  useEffect(() => {
    if (showModal) {
      setError('');
      setSuccess('');
    }
  }, [showModal]);

  return (
    <React.Fragment>
      {showModal ? (
        <div className="pageModalOverlay">
          <div className="borrowPoolWindow">
            <div className="borrowPoolWindowHeader">
              Provide more collateral
              <div className="borrowPoolWindowHeaderButton" onClick={openModal}></div>
            </div>
            <div className="borrowPoolStep">
              <div className="borrowPoolStepModalLine">
                <div className="borrowPoolStepModalLineHint">Borrowed amount</div>
                <div className="borrowPoolStepModalLineValue">{pooldata.borrowed_amount}</div>
              </div>
              <div className="borrowPoolStepModalLine">
                <div className="borrowPoolStepModalLineHint">Locked Collateral</div>
                <div className="borrowPoolStepModalLineValue">{pooldata.collateralRatio}</div>
              </div>
              <div className="borrowPoolStepModalLine borrowPoolStepModalLine2">
                <div className="borrowPoolStepHint">How much collateral would you like to provide?</div>
                <div className="borrowPoolStepModalAmount">
                  <div className="borrowPoolStepModalAmountTop">
                    <div className="borrowPoolStepModalAmountTopLeft">Amount</div>
                  </div>
                  <div className="borrowPoolStepModalAmountBottom">
                    <div className="borrowPoolStepModalAmountBottomLeft">
                      <input
                        type="text"
                        className="borrowPoolStepModalAmountBottomLeftField"
                        placeholder="Enter the Amount"
                        onChange={(e) => setBorrowAmount(e.target.value)}
                        value={borrowAmount}
                      />
                    </div>
                  </div>
                </div>
              </div>
              <div className="borrowPoolStepModalLine borrowPoolStepModalLine2 borrowPoolStepModalLine3">
                <div className="borrowPoolStepModalLineHint">New locked collateral</div>
                <div className="borrowPoolStepModalLineValue">{collateralRatio}</div>
              </div>
              <div className="error-message">{error}</div>
              <div className="success-message">{success}</div>
              <div className="borrowPoolStepModalLineButton button">{requesting ? <ActivityIndicator /> : success ? 'OK' : 'Provide Collateral'}</div>
            </div>
          </div>
        </div>
      ) : null}
    </React.Fragment>
  );
}
