import React, { useEffect, useState,useContext } from 'react'
import { useHistory } from 'react-router-dom'

import './style.css'


import Header from '../../uiElements/Header'
import DashboardTabs from '../../uiElements/Tabs/DashboardTabs'
import VoteCard from '../../uiElements/VoteCard'
import Footer from '../../uiElements/Footer'
import LendPoolDetailsVoteModal from '../../uiElements/LendPoolDetailsVoteModal'
import {lendPools} from './../../../query/pool/lendPool'
import {borrowPools} from './../../../query/pool/borrowingPools'

import { useLocation } from "react-router-dom";
import request from 'request'
import {getTotalBorrowedAmount,getBorrowRate,getTotalLendAmount,getLendRate} from "./../../../utility/dashboard"


export default function Dashboard() {

    const history = useHistory();

    let address = window.sessionStorage.getItem("address");
    const [totalBorrowAmount,setTotalBorrowAmount]= useState(0);
    const [totalBorrowRate,setTotalBorrowRate]= useState(0);
    const [totalLendAmount,setTotalLendAmount]= useState(0);
    const [totalLendRate,setTotalLendRate]= useState(0);
    const [poollendData,setpoollendData] = useState([])
    const [poolBorrowData,setpoolBorrowData] = useState([])
    useEffect(()=>{
        getTotalBorrowedAmount(address,setTotalBorrowAmount)
        getBorrowRate(address,setTotalBorrowRate)
        getTotalLendAmount(address,setTotalLendAmount)
        getLendRate(address,setTotalLendRate)

    })


    const borrowData = [
        {
            id: 1,
            borrow_rate:'$'+totalBorrowAmount,
            borrow_txt: 'Total borrowed amount',
        },
        {
            id: 2,
            borrow_rate: '$'+totalLendAmount,
            borrow_txt: 'Total lent amount',
        },
        {
            id: 3,
            borrow_rate: totalBorrowRate,
            borrow_txt: 'Total borrow rate',
        },
        {
            id: 4,
            borrow_rate: totalLendRate,
            borrow_txt: 'Total lending rate',
        }
    ]

    var stringAddress = address+"";

    if(address == null || address == '0x0000000000'){
        history.push('/')
    }



    useEffect(() => {
        borrowPools(address,setpoolBorrowData)
        lendPools(address,setpoollendData)
    },[]);

    const location = useLocation();

    const depositData = [
        {
            id: 1,
            value: '$0',
            hint: 'Deposited',
        },
        {
            id: 2,
            value: '$0',
            hint: 'Interest Earned',
        },
        {
            id: 3,
            value: '6%',
            hint: 'Interest Rate',
        }
    ]

    const creditData = [
        {
            id: 1,
            value: '$0',
            hint: 'Credit Granted',
        },
        {
            id: 2,
            value: '$0',
            hint: 'Active Credit',
        },
        {
            id: 3,
            value: '0',
            hint: 'Interest Accrued',
        },
        {
            id: 4,
            value: '6%',
            hint: 'Interest Rate',
        }
    ]


    const poolLendData = [

    ];


    const VoteData = [
        // {
        //     id: 1,
        //     txt_1: 'Vote for extension',
        //     txt_2: '@lorem_ipsum',
        //     txt_3: 'Vote ends in 3 days',
        // },
        // {
        //     id: 2,
        //     txt_1: 'Vote for extension',
        //     txt_2: '@lorem_ipsum',
        //     txt_3: 'Vote ends in 2 days',
        // },
        // {
        //     id: 3,
        //     txt_1: 'Vote for extension',
        //     txt_2: '@lorem_ipsum',
        //     txt_3: 'Vote ends in 2 days',
        // }
    ]


    const [showModal, setShowModal] = useState(false)
    const [isLoading, setIsLoading] = useState(false);


  //Hide Loading Screen setIsLoading(false);


    const openModal = () => {
        setShowModal(prev => !prev)
    }



    return (
        <React.Fragment>
            <LendPoolDetailsVoteModal openModal={openModal} showModal={showModal} />
            <Header isLoading={isLoading} />
            <div className="pageOuter">
                <div className="pageWrapper">


                <div className="dashboardLeft">

                    <div className="pageTitle">
                        Overview
                    </div>
                    <div className="cardsWrapper">

                        {borrowData.map((items, index) => (

                            <div key={index} className="dashboardCard dashboardCard2x">

                                <div className="dashboardCardValue">
                                    {items.borrow_rate}
                                </div>

                                <div className="dashboardCardHint">
                                    {items.borrow_txt}
                                </div>

                            </div>

                        ))}
                    </div>

                    <div className="pageTitle pageTitleSecondary">
                        Deposit and Save Overview
                    </div>

                    <div className="cardsWrapper">

                        {depositData.map((items, index) => (

                            <div key={index} className="dashboardCard dashboardCard3x">

                                <div className="dashboardCardValue">
                                    {items.value}
                                </div>

                                <div className="dashboardCardHint">
                                    {items.hint}
                                </div>

                            </div>

                        ))}


                        <div className="viewMoreButton button" onClick={() => history.push("/credit-lines-save")}>
                            View More
                        </div>


                    </div>



                    <div className="pageTitle pageTitleSecondary">
                        Credit Lines
                    </div>

                    <div className="cardsWrapper">

                        {creditData.map((items, index) => (

                            <div key={index} className="dashboardCard dashboardCard4x">

                                <div className="dashboardCardValue">
                                    {items.value}
                                </div>

                                <div className="dashboardCardHint">
                                    {items.hint}
                                </div>

                            </div>

                        ))}


                        <div className="viewMoreButton button" onClick={() => history.push("/credit-lines")}>
                            View More
                        </div>


                    </div>


                    <div className="pageTitle pageTitleSecondary">
                        My Pools
                    </div>


                    <div className="dashboardTabsLine">
                        <DashboardTabs poolBorrowData={poolBorrowData}  poolLendData={poolLendData}/>
                    </div>

                </div>  


                <div className="dashboardRight">

                    <div className="pageTitle">
                        Important

                        <span className="importantCounter">
                        ({VoteData.length})
                        </span>
                    </div>


                    {VoteData.map((items, index) => (
                        <VoteCard key={index} item={items} openModal={openModal} />
                    ))}

                </div>





                </div>
            </div>
            <Footer />
        </React.Fragment>
    )
}
