import React from 'react';
import { useState } from 'react';

import { useHistory } from 'react-router-dom';
import dai from './images/dai.png';
import compound from './images/compound.png';
import '@hassanmojab/react-modern-calendar-datepicker/lib/DatePicker.css';
import { Calendar, utils } from '@hassanmojab/react-modern-calendar-datepicker';
import './style.css';
import DropdownMenu from '../DropdownMenu';
import request from 'request';
import { ethers } from 'ethers';
import { requestCreditLineLender } from '../../../contractTranscations/creditLine/creditLineRequest';
import { borrowPools } from './../../../query/pool/borrowingPools';
import {
  getTotalBorrowedAmount,
  getBorrowRate,
} from './../../../utility/dashboard';

import { createOpenBorrowPool } from '../../../contractTranscations/pool/createOpenBorrowPool';
import {
  getConfiguredAddress,
  getSupportedTokens,
} from '../../../utility/functions';
import { getRequiredCollateralAmount } from '../../../query/pool/requiredCollateral';
import { calculateRepayment } from '../../../utility/poolUtility';
import { useEffect } from 'react';
import { getPoolConstants } from '../../../query/poolFactory/getConstants';
import ActivityIndicator from '../ActivityIndicator';

const addr = getConfiguredAddress('poolFactory');
const poolContract = require('../../../abi/poolFactory.json');

export default function CreateBorrowPoolModal({
  showModal,
  openModal,
  onPoolCreated,
}) {
  const history = useHistory();

  const icon = dai;
  const icon2 = compound;

  const [error, setError] = useState('');
  const [requesting, setRequesting] = useState(false);
  const [totalInterest, setTotalInterest] = useState(0);
  const [totalRepayment, setTotalRepayment] = useState(0);
  const [eachInstallment, setEachInstallment] = useState(0);
  const [collectionPeriod, setCollectionPeriod] = useState();

  const onResponseCalback = (response) => {
    if (response.error) {
      setError(response.message);
    } else if (response.success) {
      openModal();
      setFormStep(1);
      onPoolCreated();
    }

    setRequesting(false);
  };

  const depositTokenFunc = async (data) => {
    setError('');
    setRequesting(true);
    let borrowToken = DropdownBorrowToken.address;
    let CollateralToken = DropdownCollateral.address;

    const depositData = {
      borrowAmount,
      borrowToken,
      CollateralToken,
      collaterizationRatio,
      interestRate,
      loanPeriod,
      repaymentPeriod,
      description,
      requiredCollateral,
    };
    createOpenBorrowPool(depositData, onResponseCalback);
  };

  function CollateralCalculate() {
    let borrowToken = DropdownBorrowToken.value;
    let CollateralToken = DropdownCollateral.value;

    getRequiredCollateralAmount(
      borrowToken,
      CollateralToken,
      setRequiredCollateral,
      borrowAmount,
      collaterizationRatio,
    );
    getPoolConstants('collectionPeriod', setCollectionPeriod);
    setStep(4);
  }

  function updates() {
    let [totalInterest, totalRepayment, eachInstallment] = calculateRepayment(
      borrowAmount,
      interestRate,
      loanPeriod,
      repaymentPeriod,
    );
    // setRepaymentData(result)
    setTotalInterest(totalInterest);
    setTotalRepayment(totalRepayment);
    setEachInstallment(eachInstallment);
  }

  const [DropdownBorrowToken, setDropdownBorrowToken] = useState('');
  const [DropdownCollateral, setDropdownCollateral] = useState('');
  const [requiredCollateral, setRequiredCollateral] =
    useState('calculating...');

  // const dropdownData = [
  //     {
  //         value: 'ETH',
  //         icon: 'https://icons.iconarchive.com/icons/cjdowner/cryptocurrency-flat/256/Ethereum-ETH-icon.png'
  //     },
  //     {
  //         value: 'USDC',
  //         icon: 'https://messari.io/asset-images/4515ba15-2719-4183-b0ca-b9255d55b67e/128.png?v=2'
  //     },
  // ]

  // const [dropdownSelected, setDropdownSelected] = useState(dropdownData[0])
  // const [dropdownSelected2, setDropdownSelected2] = useState(dropdownData[0])

  const [formStep, setFormStep] = useState(1);

  const setStep = (index) => {
    setError('');
    setFormStep(index);
  };

  const [selectedDayRange, setSelectedDayRange] = useState({
    from: null,
    to: null,
  });

  const [showCalendar, setshowCalendar] = useState(false);

  const openCalendar = () => {
    setshowCalendar((prev) => !prev);
  };

  const [repaymentPeriod, setRepaymentPeriod] = useState('1 week');
  const [loanPeriod, setLoanPeriod] = useState('1 week');

  const changeLoanPeriod = (index) => {
    setLoanPeriod(index);
  };

  const changeRepaymentPeriod = (index) => {
    setRepaymentPeriod(index);
  };

  const [borrowAmount, setBorrowAmount] = useState('');
  const [borrowAsset, setborrowAsset] = useState('dai');
  const [collateralAsset, setcollateralAsset] = useState('compound 1');
  const [collaterizationRatio, setCollaterizationRatio] = useState('');
  const [interestRate, setInterestRate] = useState('');
  const [description, setDescription] = useState('');

  return (
    <React.Fragment>
      {showModal ? (
        <React.Fragment>
          <div className="pageModalOverlay">
            <div className="borrowPoolWindow">
              <div className="borrowPoolWindowHeader">
                Create an Open Borrow Pool
                <div
                  className="borrowPoolWindowHeaderButton"
                  onClick={openModal}
                ></div>
              </div>
              {formStep === 1 ? (
                <div className="borrowPoolStep">
                  <div className="borrowPoolStepTitle1">
                    The Open Borrow Pool
                  </div>
                  <div className="borrowPoolStepTitle2">
                    Sublime lets borrowers create their own individual loan
                    pools that are open to everyone.
                  </div>
                  <div className="borrowPoolStepParagraph">
                    <ul>
                      <li>Set the pool size & tokens supported</li>
                      <li>
                        Set the interest rate you are offering - fixed or
                        variable
                      </li>
                      <li>
                        Set the time period - fixed or indefinite terms,
                        repayment schedule
                      </li>
                      <li>Decide the collateral amount</li>
                    </ul>
                  </div>

                  <div
                    className="borrowPoolStepButton"
                    onClick={() => setStep(2)}
                  >
                    Create Open Borrow Pool
                  </div>
                </div>
              ) : null}

              {formStep === 2 ? (
                <div className="borrowPoolStep">
                  <div className="borrowPoolAmountArea">
                    <div className="borrowPoolAmountAreaTitle">Amount</div>
                    <input
                      type="text"
                      className="borrowPoolAmountAreaField"
                      placeholder="Enter the Amount"
                      onChange={(e) => setBorrowAmount(e.target.value)}
                      value={borrowAmount}
                    />
                  </div>
                  <DropdownMenu
                    dropdownSelected={DropdownBorrowToken}
                    setDropdownSelected={setDropdownBorrowToken}
                    dropdownData={getSupportedTokens('borrowTokens')}
                  />
                  <div className="borrowPoolButtonsWrapper">
                    <div
                      className="borrowPoolSmallButton borrowPoolBackButton"
                      onClick={() => setStep(1)}
                    >
                      Back
                    </div>
                    <div
                      className="borrowPoolSmallButton borrowPoolNextButton"
                      onClick={() => setStep(3)}
                    >
                      Next
                    </div>
                  </div>
                </div>
              ) : null}

              {formStep === 3 ? (
                <div className="borrowPoolStep">
                  <div className="borrowPoolStepHint">Collateral Asset</div>
                  <DropdownMenu
                    dropdownSelected={DropdownCollateral}
                    setDropdownSelected={setDropdownCollateral}
                    dropdownData={getSupportedTokens('collateralTokens')}
                  />
                  <div className="borrowPoolAmountArea">
                    <div className="borrowPoolAmountAreaTitle">
                      Collaterization Ratio
                    </div>
                    <input
                      type="text"
                      className="borrowPoolAmountAreaField"
                      placeholder="Enter Collaterization Ratio"
                      value={collaterizationRatio}
                      onChange={(e) => setCollaterizationRatio(e.target.value)}
                    />
                    <div className="borrowPoolAmountAreaRight">%</div>
                  </div>
                  <div className="borrowPoolBadge">
                    <div className="borrowPoolBadgeHint">
                      Amount getting borrowed
                    </div>
                    <div className="borrowPoolBadgeValue">
                      {borrowAmount} {DropdownBorrowToken.value}
                    </div>
                    <div className="borrowPoolBadgeHint">
                      Collaterization Ratio
                    </div>
                    <div className="borrowPoolBadgeValue">
                      {collaterizationRatio}%
                    </div>
                  </div>
                  <div className="borrowPoolButtonsWrapper">
                    <div
                      className="borrowPoolSmallButton borrowPoolBackButton"
                      onClick={() => setStep(2)}
                    >
                      Back
                    </div>
                    <div
                      className="borrowPoolSmallButton borrowPoolNextButton"
                      onClick={CollateralCalculate}
                    >
                      Next
                    </div>
                  </div>
                </div>
              ) : null}

              {formStep === 4 ? (
                <div className="borrowPoolStep">
                  <h5 className="errorMessage">{error}</h5>
                  <div className="borrowPoolAmountArea">
                    <div className="borrowPoolAmountAreaTitle">
                      Interest Rate
                    </div>
                    <input
                      type="text"
                      className="borrowPoolAmountAreaField"
                      placeholder="eg. 34%"
                      value={interestRate}
                      onChange={(e) => {
                        setInterestRate(e.target.value);
                        updates();
                      }}
                    />
                    <div className="borrowPoolAmountAreaRight">%</div>
                  </div>
                  <div className="borrowPoolAmountArea borrowPoolAmountAreaLink">
                    <div className="borrowPoolAmountAreaTitle">Loan Period</div>
                    <div className="calendatButtonsMainWrapper">
                      <div className="calendarButtonsNav calendarButtonsNavLeft button">
                        <i className="fas fa-long-arrow-alt-left"></i>
                      </div>
                      <div className="calendarButtonsWrapper calendarButtonsWrapper2">
                        <div
                          className={
                            loanPeriod === '1 week'
                              ? 'calendarButtonActive calendarButtonActive2'
                              : 'calendarButton calendarButton2'
                          }
                          onClick={() => {
                            changeLoanPeriod('1 week');
                            updates();
                          }}
                        >
                          1 week
                        </div>
                        <div
                          className={
                            loanPeriod === '2 weeks'
                              ? 'calendarButtonActive calendarButtonActive2'
                              : 'calendarButton calendarButton2'
                          }
                          onClick={() => {
                            changeLoanPeriod('2 weeks');
                            updates();
                          }}
                        >
                          2 weeks
                        </div>
                        <div
                          className={
                            loanPeriod === '3 weeks'
                              ? 'calendarButtonActive calendarButtonActive2'
                              : 'calendarButton calendarButton2'
                          }
                          onClick={() => {
                            changeLoanPeriod('3 weeks');
                            updates();
                          }}
                        >
                          3 weeks
                        </div>
                        <div
                          className={
                            loanPeriod === '4 weeks'
                              ? 'calendarButtonActive calendarButtonActive2'
                              : 'calendarButton calendarButton2'
                          }
                          onClick={() => {
                            changeLoanPeriod('4 weeks');
                            updates();
                          }}
                        >
                          4 weeks
                        </div>
                        <div
                          className={
                            loanPeriod === '5 weeks'
                              ? 'calendarButtonActive calendarButtonActive2'
                              : 'calendarButton calendarButton2'
                          }
                          onClick={() => {
                            changeLoanPeriod('5 weeks');
                            updates();
                          }}
                        >
                          5 weeks
                        </div>
                      </div>
                      <div className="calendarButtonsNav calendarButtonsNavRight button">
                        <i className="fas fa-long-arrow-alt-right"></i>
                      </div>
                    </div>
                  </div>
                  <div className="borrowPoolAmountArea borrowPoolAmountAreaLink">
                    <div className="borrowPoolAmountAreaTitle">
                      Repayment Period
                    </div>
                    <div className="calendarButtonsWrapper">
                      <div
                        className={
                          repaymentPeriod === '1 week'
                            ? 'calendarButtonActive'
                            : 'calendarButton'
                        }
                        onClick={() => {
                          changeRepaymentPeriod('1 week');
                          updates();
                        }}
                      >
                        1 week
                      </div>
                      <div
                        className={
                          repaymentPeriod === '2 weeks'
                            ? 'calendarButtonActive'
                            : 'calendarButton'
                        }
                        onClick={() => {
                          changeRepaymentPeriod('2 weeks');
                          updates();
                        }}
                      >
                        2 weeks
                      </div>
                      <div
                        className={
                          repaymentPeriod === '4 weeks'
                            ? 'calendarButtonActive'
                            : 'calendarButton'
                        }
                        onClick={() => {
                          changeRepaymentPeriod('4 weeks');
                          updates();
                        }}
                      >
                        4 weeks
                      </div>
                      <div
                        className={
                          repaymentPeriod === 'lone period'
                            ? 'calendarButtonActive calendarButtonActiveLone'
                            : 'calendarButton calendarButtonLone'
                        }
                        onClick={() => {
                          changeRepaymentPeriod('loan period');
                          updates();
                        }}
                      >
                        End on loan period
                      </div>
                    </div>
                  </div>
                  <div className="borrowPoolGreyPad">
                    <div className="borrowPoolGreyPadLine">
                      <div className="borrowPoolGreyPadLineHint">
                        Collateral needed
                      </div>
                      <div className="borrowPoolGreyPadLineValue">
                        {Number(requiredCollateral).toFixed(2)}{' '}
                        {DropdownCollateral.value}
                      </div>
                    </div>
                    <div className="borrowPoolGreyPadLine">
                      <div className="borrowPoolGreyPadLineHint">
                        Loan amount requested
                      </div>
                      <div className="borrowPoolGreyPadLineValue">
                        {Number(borrowAmount).toFixed(2)}{' '}
                        {DropdownBorrowToken.value}
                      </div>
                    </div>
                    <div className="borrowPoolGreyPadLine">
                      <div className="borrowPoolGreyPadLineHint">
                        Total Repayment
                      </div>
                      <div className="borrowPoolGreyPadLineValue">
                        {Number(totalRepayment).toFixed(2)}{' '}
                        {DropdownBorrowToken.value}
                      </div>
                    </div>
                    <div className="borrowPoolGreyPadLine">
                      <div className="borrowPoolGreyPadLineHint">
                        Each Instalment
                      </div>
                      <div className="borrowPoolGreyPadLineValue">
                        {Number(eachInstallment).toFixed(2)}{' '}
                        {DropdownBorrowToken.value}
                      </div>
                    </div>
                    <div className="borrowPoolGreyPadLine">
                      <div className="borrowPoolGreyPadLineHint">
                        Total Interest
                      </div>
                      <div className="borrowPoolGreyPadLineValue">
                        {Number(totalInterest).toFixed(2)}{' '}
                        {DropdownBorrowToken.value}
                      </div>
                    </div>
                  </div>
                  <div className="borrowPoolSmallHint">
                    Lenders will have {collectionPeriod} to deposit liquidity in
                    this pool
                  </div>
                  <div className="borrowPoolButtonsWrapper">
                    <div
                      className="borrowPoolSmallButton borrowPoolBackButton"
                      onClick={() => setStep(3)}
                    >
                      Back
                    </div>
                    <div
                      className="borrowPoolSmallButton borrowPoolNextButton"
                      onClick={() => depositTokenFunc('data')}
                    >
                      {requesting ? <ActivityIndicator /> : 'Create Pool'}
                    </div>
                  </div>
                </div>
              ) : (
                ''
              )}

              {formStep === 5 ? (
                <div className="borrowPoolStep">
                  <div className="borrowPoolNoteWrapper">
                    <div className="borrowPoolNoteTitle">
                      You can add a description for the pool here
                    </div>
                    <textarea
                      className="borrowPoolNoteTextArea"
                      placeholder="eg. funds will be used for yield farming"
                      value={description}
                      onChange={(e) => setDescription(e.target.value)}
                    ></textarea>
                  </div>
                  <div
                    className="borrowPoolStepButton"
                    onClick={() => depositTokenFunc('data')}
                  >
                    Deposit token amount and create pool
                  </div>
                </div>
              ) : null}
            </div>
          </div>
        </React.Fragment>
      ) : null}
    </React.Fragment>
  );
}
