import request from 'request'
import {getSymbol} from './../../utility/symbol'
import {getLoanStartDay,countdown} from './../../utility/time'
import {ethers} from 'ethers'
import {SUBGRAPH_API_URL} from '../../utility/constants'


export function lendMoreDetailPools(address,setpooldata){

    let result;
    const data = JSON.stringify({
        query: `  {
            pools(where:{id:"${address}"}) {
              id
              idealCollateralRatio
              borrowAmountRequested
              collateralAsset
              borrowAsset
              borrowRate
              borrowAmountRequested
              lentAmount
              loanStartTime
              repaymentInterval
              published
              noOfRepaymentIntervals
              borrower{
                id 
                twitterDetails{
                    twitterId
                }
              }
            }
          }

          `
    })

    var options = {
        'method': 'POST',
        'url': SUBGRAPH_API_URL,
        'headers': {'Content-Type': 'application/json'},
        'body': data
    }       


    result = request(options, async function (error, response) {

        if (error) throw new Error(error); 
        var body = JSON.parse(response.body)
        var pools = []
        if(body){
            if(body.data){
                if(body.data.pools && body.data.pools.length>0){
                    if(body.data.pools[0]){
                        let poolData = body.data.pools[0]

                        var pool = {
                                    collateralRatio: (poolData.idealCollateralRatio)/10**8 +'%',
                                    borrowed_amount: ((poolData.borrowAmountRequested)/10**18)+ ' '+getSymbol(poolData.borrowAsset),
                                    collateralAssest: getSymbol(poolData.collateralAsset),
                                    BorrowAssest: getSymbol(poolData.borrowAsset),
                                    borrowRate: (poolData.borrowRate)/10**8+'%',
                                    collectionProgress:Math.floor(poolData.lentAmount*100/poolData.borrowAmountRequested)+'%',
                                    collectionEnds: getLoanStartDay( poolData.loanStartTime),
                                    RepaymentFrequency:countdown(poolData.repaymentInterval),
                                    publishedDate: new Date(parseInt(poolData.published)*1000),
                                    LoanPeriod:countdown(poolData.repaymentInterval*poolData.noOfRepaymentIntervals),
                                    name: ethers.utils.parseBytes32String(poolData.borrower.twitterDetails.twitterId),
                                    username: '@'+ethers.utils.parseBytes32String(poolData.borrower.twitterDetails.twitterId)
                                    }
                        setpooldata(pool)
                    }
                }
            }

        }

    })

};
