import request from 'request'




export function pendingCreditLine(address,setCreditLinePending){

    let result;

    console.log("function called");

    const data = JSON.stringify({
        query: `{
            creditLines(where:{creditLineStatus:"REQUESTED",lender:"${address}"}){
              id
              creditLineStatus
              lender
              BorrowLimit
              collateralAsset
              borrowRate
              idealCollateralRatio
              liquidationThreshold
              autoLiquidation
            }
          }
          `
    })


    var options = {
        'method': 'POST',
        'url': 'https://api.thegraph.com/subgraphs/name/sublime-finance/sublime',
        'headers': {'Content-Type': 'application/json'},
        'body': data
    }

    result = request(options, async function (error, response) {

        if (error) throw new Error(error); 
        var body = JSON.parse(response.body)
        console.log(body)   
        setCreditLinePending(body.data.creditLines)
    })

};
