import React, { useState, useEffect } from 'react'
import { addCollateralInMarginCall } from '../../../contractTranscations/pool/addCollateralInMarginCall'
import {collateralborrowPools} from '../../../query/pool/addCollateral'
import ActivityIndicator from '../ActivityIndicator'

import './style.css'

export default function RespondToMarginCallModal({selectedPool, showModal, openModal}) {
    const [amount, setAmount] = useState('')
    const [requesting, setRequesting] = useState(false);
    const [transferSavingAccount, settransferSavingAccount] = useState(false)
    const [pooldata, setpooldata] = useState([])
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const onResponseCalback = (response) => {
        if (response.error) {
            setError(response.message)
        } else if (response.success) {
            setSuccess('You have executed successfully.')
        }
        setRequesting(false)
    }

    useEffect(() => {
        if (showModal) {
            setError('')
            setSuccess('')
            collateralborrowPools(selectedPool.address, setpooldata)
        }
    }, [showModal]);

    function onExecute(){
        if (success) {
            setSuccess('')
            openModal()
        } else {
            if (amount) {
                setError('')
                setSuccess('')
                setRequesting(true)
                addCollateralInMarginCall(
                    amount,
                    selectedPool.address,
                    pooldata.collateralAssest.address,
                    transferSavingAccount,
                    onResponseCalback
                );
            }
        }
    }

    return (
        <React.Fragment>
        {showModal ?
            <div className="pageModalOverlay">
                <div className="borrowPoolWindow">
                    <div className="borrowPoolWindowHeader">
                        Respond to a Margin Call
                        <div className="borrowPoolWindowHeaderButton" onClick={openModal}></div>
                    </div>
                    <div className="borrowPoolStep">
                    <div className="borrowPoolStepModalLine">
                            <div className="borrowPoolStepModalLineHint">Lender</div>
                            <div className="borrowPoolStepModalLineValue">{selectedPool?.name}</div>
                        </div>
                        <div className="borrowPoolStepModalLine">
                            <div className="borrowPoolStepModalLineHint">Amount Lent</div>
                            <div className="borrowPoolStepModalLineValue">100 DAI</div>
                        </div>
                        <div className="borrowPoolStepModalLine">
                            <div className="borrowPoolStepModalLineHint">Collateral Amount</div>
                            <div className="borrowPoolStepModalLineValue">$50</div>
                        </div>
                        <div className="borrowPoolStepModalLine">
                            <div className="borrowPoolStepModalLineHint">Current Collateral Ratio</div>
                            <div className="borrowPoolStepModalLineValue">{pooldata?.collateralRatio}</div>
                        </div>
                        <div className="borrowPoolStepModalLine">
                            <div className="borrowPoolStepModalLineHint">Ideal Collateral Ratio</div>
                            <div className="borrowPoolStepModalLineValue">73%</div>
                        </div>
                        <div className="borrowPoolStepModalLine">
                            <div className="borrowPoolStepModalLineHint">Amount Required to close Margin Call</div>
                            <div className="borrowPoolStepModalLineValue">73%</div>
                        </div>
                        <div className="borrowPoolStepModalAmount">
                            <div className="borrowPoolStepModalAmountTop">
                                <div className="borrowPoolStepModalAmountTopLeft">
                                    Collateral Amount
                                </div>
                                <div className="borrowPoolStepModalAmountTopRight"></div>
                            </div>
                            <div className="borrowPoolStepModalAmountBottom">
                                <div className="borrowPoolStepModalAmountBottomLeft">
                                    <input
                                        type="text"
                                        className="borrowPoolStepModalAmountBottomLeftField"
                                        placeholder="Amount you want to repay"
                                        onChange={e => setAmount(e.target.value)}
                                        value={amount}
                                    />
                                </div>
                            </div>
                        </div>
                        <div className="borrowPoolStepModalLine borrowPoolStepModalLineMargin">
                            <div className="borrowPoolStepModalLineHint">Transfer from your Saving Account</div>
                            <div className="borrowPoolStepModalLineValue">
                                <input
                                    type="checkbox"
                                    checked={transferSavingAccount}
                                    onChange={(e) => settransferSavingAccount(prev => !prev)}
                                />
                            </div>
                        </div>
                        <div className="error-message">{error}</div>
                        <div className="success-message">{success}</div>
                        <div className="lendPoolStepModalLineButton button" onClick={onExecute}>
                            {requesting ? <ActivityIndicator /> : success ? 'OK' : 'Execute'}
                        </div>
                    </div>
                </div>
            </div>
            : null
        }
    </React.Fragment>
    )
}
