import request from 'request'

import {getLoanStartDay} from './../../utility/time'
import {getSymbol} from './../../utility/symbol'
import {SUBGRAPH_API_URL} from '../../utility/constants'
import {ethers} from 'ethers'

export function borrowPools(address,setpoolBorrowData){

    let result;

    const data = JSON.stringify({
        query: `{
            users(where:{id:"${address}"} ){
              id
              borrowingPools {
                id
                borrower{
                    twitterDetails{
                        twitterId
                    }
                }
                borrowRate
                borrowAmountRequested
                loanStatus
                published
                loanStartTime
                lentAmount
                borrowAsset
              }
            }
          }
          `
    })

    var options = {
        'method': 'POST',
        'url': SUBGRAPH_API_URL,
        'headers': {'Content-Type': 'application/json'},
        'body': data
    }       


    result = request(options, async function (error, response) {

        if (error) throw new Error(error); 
        var body = JSON.parse(response.body)
        var pools = []
        if(body){
            if(body.data){
                if(body.data.users && body.data.users.length>0){
                    if(body.data.users[0].borrowingPools){
                        let poolData = body.data.users[0].borrowingPools
                        for(var i =0;i<poolData.length;i++){
                            let name = 'user name'
                            try {
                                name = ethers.utils.parseBytes32String(poolData[i].borrower.twitterDetails.twitterId)
                            } catch (error) {}
                            var pool = {
                                id: i+1,
                                avatar: '',
                                name,
                                username: `@${name}`,
                                pool_type: 'Open Borrow',
                                pool_status: poolData[i].loanStatus,
                                // pool_status: 'Active',
                                created_on: '18th Jan 2021',
                                borrow_rate: (poolData[i].borrowRate)/10**8+'%',
                                borrowed_amount: (poolData[i].borrowAmountRequested)/10**18+' '+ getSymbol(poolData[i].borrowAsset),
                                progress_and_payment_txt: 'Collection Progress',
                                progress_and_payment: Math.floor((poolData[i].lentAmount*100)/poolData[i].borrowAmountRequested)+'%',
                                next_payment: '200 DOT',
                                ends_date: getLoanStartDay( poolData[i].loanStartTime),
                                btn_1: 'Provide collateral',
                                btn_2: 'View More',
                                address:poolData[i].id
                            }
                            pools.push(pool)

                        }
                    }
                }
            }

        }
        setpoolBorrowData(pools)
    })

};
