import React from 'react';
import { useState, useEffect } from 'react';
import Header from '../../uiElements/Header';
import ActivityIndicator from '../../uiElements/ActivityIndicator';
import BorrowTabs from '../../uiElements/Tabs/BorrowTabs';
import Footer from '../../uiElements/Footer';
import { useHistory } from 'react-router-dom';
import './style.css';
import SendCreditLineModal from '../../uiElements/SendCreditLineModal';
import { requestCreditLineLender } from '../../../contractTranscations/creditLine/creditLineRequest';
import { borrowPools } from './../../../query/pool/borrowingPools';
import {
  getTotalBorrowedAmount,
  getBorrowRate,
} from './../../../utility/dashboard';

export default function Borrow() {
  const history = useHistory();
  const [isLoading, setIsLoading] = useState(false);
  const [poolBorrowData, setpoolBorrowData] = useState([]);
  const [totalBorrowAmount, setTotalBorrowAmount] = useState(0);
  const [totalBorrowRate, setTotalBorrowRate] = useState(0);

  const address = window.sessionStorage.getItem('address');

  if (address == null || address === '0x0000000000') {
    history.push('/');
  }

  const onData = (data) => {
    setpoolBorrowData(data);
    setIsLoading(false);
  };

  useEffect(() => {
    setIsLoading(true);
    borrowPools(address, onData);
    getTotalBorrowedAmount(address, setTotalBorrowAmount);
    getBorrowRate(address, setTotalBorrowRate);
  }, []);

  const borrowData = [
    {
      id: 1,
      borrow_rate: totalBorrowRate + '%',
      borrow_txt: 'Current borrow rate',
    },
    {
      id: 2,
      borrow_rate: '$' + totalBorrowAmount,
      borrow_txt: 'Total borrowed amount',
    },
    {
      id: 3,
      borrow_rate: '1,500 DAI',
      borrow_txt: 'Next repayment on 25th Jan 2021',
      pay: 'Pay',
    },
  ];

  const [showModal, setShowModal] = useState(false);

  const sendCreditLineRequest = (args) => {
    // alert('sendCreditLineRequest');
    requestCreditLineLender(args);
    openModal();
  };

  const openModal = () => {
    setShowModal((prev) => !prev);
  };

  return (
    <React.Fragment>
      <SendCreditLineModal
        showModal={showModal}
        openModal={openModal}
        sendCreditLineRequest={sendCreditLineRequest}
      />
      <Header />
      <div className="pageOuter">
        <div className="pageWrapper pageWrapperPadding">
          <div className="pageTitle">Borrow</div>
          <div className="cardsWrapper">
            {borrowData.map((items, index) => (
              <div key={index} className="dashboardCard dashboardCard3x">
                <div className="dashboardCardValue">{items.borrow_rate}</div>

                <div className="dashboardCardHint">{items.borrow_txt}</div>
              </div>
            ))}
          </div>
          <div className="pageTitle pageTitleSecondary">My Pools</div>
          {isLoading ? (
            <div className="d-flex align-items-center">
              <ActivityIndicator />
            </div>
          ) : (
            <div className="dashboardTabsLine">
              <BorrowTabs
                poolData={poolBorrowData}
                openModalRequest={openModal}
              />
            </div>
          )}
        </div>
      </div>
      <Footer />
    </React.Fragment>
  );
}
