import React, { useEffect } from 'react'
import {useState} from 'react'
import './style.css'
import Footer from '../../uiElements/Footer'
import CreditLinesSaveItem from '../../uiElements/CreditLinesSaveItem'
import CreditLinesSaveTabs from '../../uiElements/Tabs/CreditLinesSaveTabs'
import { deposit } from '../../../contractTranscations/savingsAccount'
import { withdraw } from '../../../contractTranscations/withdrawAccount'

const creditLinesSaveItemData = [
    {
        assetName: 'TST',
        address:"0x463514eA551b88F176dC9e71E529DD02eb2D0cF8",
        logo: 'https://messari.io/asset-images/4515ba15-2719-4183-b0ca-b9255d55b67e/128.png?v=2',
        balance: '1,500 COMP',
        deposited: '1,000 COMP',
        depositedUsd: '$2240.20',
        rate: '10.5%',
        interest: '500 COMP',
        interestUsd: '$865.00',
        depositFee: '0.17 ETH',
        withdrawFee: '0.18 ETH',
        depositFeeUsd: '$12.25',
        withdrawFeeUsd: '$10.90',
    },
    {
        assetName: 'TST 1',
        address:"0xab2af84a9db35f92dfb5b0607bd91226f5e97469",
        logo: 'https://icons.iconarchive.com/icons/cjdowner/cryptocurrency-flat/256/Ethereum-ETH-icon.png',
        balance: '5,200 AAVE',
        deposited: '3,500 AAVE',
        depositedUsd: '$2240.20',
        rate: '7.5%',
        interest: '1500 AAVE',
        interestUsd: '$865.00',
        depositFee: '0.21 ETH',
        withdrawFee: '0.22 ETH',
        depositFeeUsd: '$12.25',
        withdrawFeeUsd: '$10.90',
    }
]

const dropdownData = [
    {
        value: 'COMP',
        address:"0xC6Cfc33063d34a3411Ff74981c8494D12BF4fA1a",
        icon: 'https://cryptologos.cc/logos/compound-comp-logo.png'
    },
    {
        value: 'AAVE',
        address:"0x81cb9e3bf74A66d167aE5AeB5332011F157Ac45F",
        icon: 'https://cryptologos.cc/logos/aave-aave-logo.png'
    },
]

export default function CreditLinesSave() {
    const totalValues = {
        deposited: '$0.00',
        interestEarned: '$0.00'
    }

    const [selected, setSelected] = useState('');
    const [depositAmount, setDepositAmount] = useState('')
    const [withdrawAmount, setWithdrawAmount] = useState('')
    const [requesting, setRequesting] = useState(false)
    const [error, setError] = useState('')
    const [success, setSuccess] = useState('')
    const [selectedAsset, setSelectedAsset] = useState(creditLinesSaveItemData[0]);
    const [dropdownSelected, setDropdownSelected] = useState(dropdownData[0])

    useEffect(() => {
        setSuccess('')
        setError('')
    }, [])

    const onResponseCalback = (response) => {
        if (response.error) {
            setError(response.message)
        } else if (response.success) {
            setSuccess(`You have ${response.type} successfully.`)
        }
        setRequesting(false)
    }

    const onDeposit = () => {
        if (success) {
            setSuccess('')
        } else {
            if (depositAmount) {
                setError('')
                setSuccess('')
                setRequesting(true)
                deposit(depositAmount, selectedAsset.address, '0x0000000000000000000000000000000000000000', '', onResponseCalback)
            }
        }
    }

    const onWithdraw = () => {
        if (success) {
            setSuccess('')
        } else {
            if (withdrawAmount) {
                setError('')
                setSuccess('')
                setRequesting(true)
                withdraw(withdrawAmount, selectedAsset.address, '0x0000000000000000000000000000000000000000', false, onResponseCalback)
            }
        }
    }

    const changeSelectedAsset = (data) => {
        setSelectedAsset(data);
        setDepositAmount(0);
        setWithdrawAmount(0);
    }

    return (
        <React.Fragment>
            <div className="pageOuter">
                <div className="pageWrapper pageWrapperCreditLinesSave">
                    <div className="CreditLinesSaveLeft">
                        <div className="pageTitle">
                            Save
                        </div>
                        <div className="cardsWrapper">
                            <div  className="dashboardCard dashboardCard2x">
                                <div className="dashboardCardValue">
                                    {totalValues.deposited}
                                </div>
                                <div className="dashboardCardHint">
                                    Deposited
                                </div>
                            </div>
                            <div  className="dashboardCard dashboardCard2x">
                                <div className="dashboardCardValue">
                                    {totalValues.interestEarned}
                                </div>
                                <div className="dashboardCardHint">
                                    Interest Earned
                                </div>

                            </div>
                        </div>
                        <div className="creditLinesTable">
                            <div className="creditLinesTableTop">
                                <div className="creditLinesTableTopItem creditLinesTableTopItemLeft creditLinesTableTopItem4x">
                                All Assets
                                </div>
                                <div className="creditLinesTableTopItem creditLinesTableTopItem4x">
                                Deposited
                                </div>
                                <div className="creditLinesTableTopItem creditLinesTableTopItem4x">
                                Rate
                                </div>
                                <div className="creditLinesTableTopItem creditLinesTableTopItem4x">
                                Interest Earned
                                </div>
                            </div>
                            {creditLinesSaveItemData.map((item, index) => (
                                <CreditLinesSaveItem
                                    key={index}
                                    data={item}
                                    changeSelectedAsset={changeSelectedAsset}
                                    itemKey={index}
                                    selected={selected}
                                    setSelected={setSelected}
                                />
                            ))}
                        </div>
                    </div>
                    <div className="CreditLinesSaveRight">
                        <div className="CreditLinesSaveRightTitle">
                            {selectedAsset.assetName}
                        </div>
                        <CreditLinesSaveTabs
                            success={success}
                            requesting={requesting}
                            deposit={onDeposit}
                            withdraw={onWithdraw}
                            data={selectedAsset}
                            depositAmount={depositAmount}
                            setDepositAmount={setDepositAmount}
                            withdrawAmount={withdrawAmount}
                            setWithdrawAmount={setWithdrawAmount}
                            dropdownData={dropdownData}
                            dropdownSelected={dropdownSelected}
                            setDropdownSelected={setDropdownSelected}
                        />
                        <div className="error-message">{error}</div>
                        <div className="success-message">{success}</div>
                    </div>
                </div>
            </div>
            <Footer />
        </React.Fragment>
    )
}
